function generateGridLayout(gridSize, elementSizes) {
  const layout = {};
  const elements = elementSizes.map((size, index) => ({ id: index, size }));
  elements.sort((a, b) => a.size - b.size);

  for (let i = 0; i < elements.length; i++) {
    const element = elements[i];
    layout[element.id] = {
      x: Math.floor(Math.random() * (gridSize - element.size)),
      y: Math.floor(Math.random() * (gridSize - element.size)),
    };
  }

  return layout;
}
